} from 'react';
import CardList from './CardList';
import {
    robots
} from './robots';
import SearchBox from './SearchBox';



class App extends Component {
    constructor() {
        super()
        this.state = {
            robots: robots,
            searchfield: ''
        }
    }

    onSearchChange(event) {

        console.log(event);
        // const filterRobots = this.state.robots.filter(robots => {
        //     return robots.name.toLowerCase()

        // })
    }

    render() {
        return ( <
            div className = 'tc' >
            <
            h1 > RoboFriends < /h1> <
            SearchBox onChange = {
                this.onSearchChange
            }
            /> <
            CardList robots = {
                this.state.robots
            }
            /> <
            /div>
        );
    }
}
export default App;
